definition.script:
pipeline {
   agent any

   stages {
      stage('Cleaning') {
         steps {
            // Run Maven on a Unix agent.
            dir ("${WORKSPACE}")
            {
            sh "rm -rf *"
            }
         }
      }
      stage('CheckOut') {
         steps {
            // Run Maven on a Unix agent.
            git 'https://github.com/jyonarra/devops.git'
            dir ("${WORKSPACE}")
            {
            sh "chmod 755 *"
            sh "./testGitJdk.sh $java_name"
            }
         }
      }
      stage('Build') {
         steps {
            dir ("${WORKSPACE}")
            {
            sh "./maven_build.sh"
            }
        }
      }
      stage('Sonar Analysis') {
         steps {
            dir ("${WORKSPACE}")
            {
            sh "./sonar.sh sonar.properties"
            }
        }
      }
      stage('Upload Artifact') {
         steps {
            dir ("${WORKSPACE}")
            {
            sh "./artifactory_build.sh $BUILD_NUMBER"
            }
        }
      }
      stage('Artifactory Download in Stage') {
         steps {
            dir ("${WORKSPACE}")
            {
            sh "./artifactory_build_download.sh $BUILD_NUMBER"
            }
        }
      }
      stage('Server Deployment') {
         steps {
            dir ("${WORKSPACE}")
            {
            sh "./deploy-app.sh $ENV"
            sh "sleep 5"
            }
        }
      }
   }
}
